import React, {useState, useEffect} from 'react'
import {connect} from 'react-redux';

function Todo(props){

  console.log(getState());

  const [todoItems, setTodoItems] = useState();


  useEffect(()=>{
    let todosData = props.todo.then((result)=>{
      return result.map(item=>{
        return <li key={item.id}>{item.body}</li>
      })
    })
    todosData.then((result)=>{setTodoItems(result)});
  }, [])

    return(
      <div id="todo-container">
        <p className="container-title red-title">Todo</p>
        <ul id="todo-list">
          {todoItems || <p style={{color: 'rgba(100, 100, 100, 0.8)', margin: '70px 0px', textShadow: '1px 2px 10px gray'}}>Nothing To Do !</p>}
        </ul>
      </div>
    )
}

const mapStateToProps = (state, ownProps)=>{
  return{
    todo: state.todo
  }
}

export default connect(mapStateToProps)(Todo);
