import React, {useState, useEffect} from 'react';

import Todo from './components/todo.js'
import InProgress from './components/inprogress.js'
import Done from './components/done.js'
import './App.css';

function App() {
  //some text comes from the server
  const [text, setText] = useState('some text');
  //newTodoLayout display style (display/hide)
  const [newTodoDisplay, setNewTodoDisplay] = useState('none');


  //call 'api/hello' to try server response
  const callApi = async ()=>{
    const response = await fetch('/api/hello');
    const body = await response.json();

    if(response.status != 200) throw Error(body.message)

    return body;
  }

  useEffect(()=>{
    callApi().then(res=>{
      setText(res.express)
    })
  }, [])

  //display/hide newTodoLayout container
  function newTodoLayoutDisplay(e){
    (newTodoDisplay == 'none')? setNewTodoDisplay('flex'): setNewTodoDisplay('none');
  }

  function newTodoInputHandle(e){

  }

  return (
    <div className="App">
      <h2 id="title" contenteditable="true">Call out API</h2>
      <p id="description">{text}</p>
      <div id="manager">
        <Todo />
        <InProgress />
        <Done />
      </div>
      <button id="new-todo-input-display" onClick={newTodoLayoutDisplay}>+</button>
      <div id="new-todo-input" style={{display: newTodoDisplay}}>
        <input type="text" value="" onChange={newTodoInputHandle} placeholder="new todo.." />
        <button>Add</button>
      </div>
    </div>
  );
}

export default App;
