import {fetchTodo, fetchInprogress, fetchDone} from '../requests/fetchApi'

const initState = {
  todo: [],
  inprogress: [],
  done: [],
  loader: false,
  err: false
};

const rootReducer = (state = initState, action)=>{
console.log(state);
  switch(action.type){
    //data status part
    case 'gettodorequested':
      return{
        ...state,
        loader: true
      }
      break;
      case 'gettodosucceeded':
        return{
          ...state,
          loader: false,
          err: false
        }
        break;
      case 'getinprogressrequested':
        return{
          ...state,
          loader: true
        }
        break;
      case 'getinprogresssucceeded':
        return{
        ...state,
        loader: false,
        err: false
      }
        break;

    case 'todo-data':
      return{
        ...state,
        todo: action.body
      }
      break;
    case 'inprogress-data':
      return{
        ...state,
        inprogress: action.body
      }
      break;
    case 'done-data':
      return{
        ...state,
        done: action.body
      }
      break;
    case 'add_todo':
      return{
        ...state,
        todo: [...state.todo, action.body]
      }
      break;

    default:
      return state;
  }

}

export default rootReducer
