import React, {useState, useEffect} from 'react'
import {connect} from 'react-redux';

import {fetchTodo} from '../requests/fetchApi'
import {todoCommit} from '../actions/getDataActions'

function Todo(props){
  const [fetchedTodo, setFetchedTodo] = useState('');
  const [todoItems, setTodoItems] = useState('');
  // const [dataStatus, setDataStatus] = useState(GET_TODO_REQUESTED);

  // get data after the first render
  useEffect(()=>{
      fetchTodo().then((result)=>{
      props.todoCommit({body: result})
    })
  }, [])

  // map data when ever fetchedTodo is updated
  useEffect(()=>{
    const renderTodo = Array.from(fetchedTodo).reverse().map((item)=>{
      return <li key={item.id}>{item.body}</li>
    })
    setTodoItems(renderTodo)
  }, [fetchedTodo])

  // update data when ever store is updated
  useEffect(()=>{
    setFetchedTodo(props.todo)
  }, [props.todo])


    return(
      <div id="todo-container">
        <p className="container-title red-title">Todo</p>
        <ul id="todo-list">
          {todoItems || <div><div id="spinner"></div> <p style={{color: 'gray'}}>fetching data..</p></div>}
        </ul>
      </div>
    )
}

const mapStateToProps = (state, ownProps)=>{
  const {todo} = state;
  return{
    todo: todo
  }
}
const mapDispatchToProps = (dispatch)=>{
  return{
    todoCommit: (data)=>{
      dispatch(todoCommit(data))
    },
    todoDataStatus: ()=>{
      dispatch
    }
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Todo);
