import {connect} from 'react-redux'

function Done(props){

  const [done, setDone] = useState('');

  useEffect(()=>{
    const doneTodo = props.done.then()
  }, [])

  return(
    <div id="done-container">
      <p class="container-title green-title">Done</p>
      <ul id="done-list">
        {done || <p style={{color: 'rgba(100, 100, 100, 0.8)', margin: '70px 0px', textShadow: '1px 2px 10px gray'}}>Nothing Done Yet !</p>}
      </ul>
    </div>
  )
}

const mapStateToProps = (state, ownProps)=>{
  return{
    done: state.done
  }
}

export default connect(mapStateToProps)(Done)
