import {fetchTodo, fetchInprogress, fetchDone} from '../requests/fetchApi'

const initState = {
  todo: [],
  inprogress: [],
  done: []
};

const rootReducer = (state = initState, action)=>{
  console.log(action);

  switch(action.type){
    case 'todo-data':
      return{
        ...state,
        todo: action.body
      }
      break;

    case 'inprogress-data':
      return{
        ...state,
        inprogress: action.body
      }
      break;

