export const fetchTodo = async ()=> {
  const call = await fetch('/todo');
  const response = await call.json();

  return response;
}

export const fetchInprogress = async ()=> {
  const call = await fetch('/inprogress');
  const response = await call.json();

  return response;
}

export const fetchDone = async ()=> {
  const call = await fetch('/done');
  const response = await call.json();

  return response;
}

