function Item(){

  const [item, setItem] = useState('')

  const itemApiCall = async ()=>{
    const response = await fetch('/items');
    const body = await response.json();
    if(response.status != 200) throw Error(body.message)
    return body;
  }

  useEffect(()=>{
    setTimeout(()=>{

    }, 2000)
    itemApiCall().then(res=>{
      const itemMap = res.map(item=>{
        return (
          <li>{item.body}</li>
        )
      })
      setItem(itemMap)
    })
  }, [])

    return(
      <ul>
        {item}
      </ul>
    )
}

export default Item
