const getTodos = async (link)=>{
  const callTodos = await fetch(link);
  const response = await callTodos.json();
  if(callTodos.status !== 200) return new Error(response.message);

  return response.reverse();
}

let resultTodo = getTodos('/todos').then(result=> {return result; });
let resultInprogress = getTodos('/inprogress').then(result=> {return result});
let resultDone = getTodos('/done').then(result=> {return result});
const initState = {
  todo: resultTodo,
  inprogress: resultInprogress,
  done: resultDone
};

const rootReducer = (state = initState, action)=>{
  // console.log(action);
  if(action.type == 'add todo'){
    console.log(state.todo);
    return {
      ...state,
      todo: [action.body, ...state.todo]
    }
    console.log(state);
  }else{
    return state
  }

}

export default rootReducer
