import {fetchTodo, fetchInprogress, fetchDone} from '../requests/fetchApi'

const resultOfTodo = fetchTodo().then((result)=>{return result})
const resultOfInprogress = fetchInprogress().then((result)=>{return result})
const resultOfDone = fetchDone().then((result)=>{return result})

const initState = {
  todo: resultOfTodo,
  inprogress: resultOfInprogress,
  done: resultOfDone
};

const rootReducer = (state = initState, action)=>{
  // console.log(action);
  console.log({...state});
  if(action.type == 'add todo'){

    state.todo.then((result)=>{
      console.log(result);
      return{
        todo: [...result,]
      }
    })

  }else{
    return state
  }

}

export default rootReducer
