import React, {useState, useEffect} from 'react'
import {connect} from 'react-redux';

import {GET_TODO_REQUESTED, GET_TODO_SUCCEEDED, GET_TODO_FAILED} from '../requests/fetchState'
import {fetchTodo} from '../requests/fetchApi'

function Todo(props){
  console.log(props.todo);
  const [fetchedTodo, setFetchedTodo] = useState('');
  const [todoItems, setTodoItems] = useState('');

  useEffect(()=>{
      fetchTodo().then((result)=>{
      props.todoCommit({type: 'todo-data', body: result})
    })
  }, [])

  useEffect(()=>{
    const renderTodo = Array.from(fetchedTodo).map((item)=>{
      return <li key={item.id}>{item.body}</li>
    })
    setTodoItems(renderTodo)
  }, [fetchedTodo])

  useEffect(()=>{

  }, [props.todo])

    return(
      <div id="todo-container">
        <p className="container-title red-title">Todo</p>
        <ul id="todo-list">
          {todoItems || <div><div id="spinner"></div> <p style={{color: 'gray'}}>fetching data..</p></div>}
        </ul>
      </div>
    )
}

const mapStateToProps = (state, ownProps)=>{
  return{
    todo: state.todo
  }
}
const mapDispatchToProps = (dispatch)=>{
  return{
    todoCommit: (data)=>{
      return dispatch(data)
    }
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Todo);
