import React, {useState, useEffect} from 'react'
import {connect} from 'react-redux';

import {GET_TODO_REQUESTED, GET_TODO_SUCCEEDED, GET_TODO_FAILED} from '../requests/fetchState'
import {fetchTodo, fetchInprogress, fetchDone} from '../requests/fetchApi'

function Todo(props){


  const [todoItems, setTodoItems] = useState();


  useEffect(()=>{
    const todos = fetchTodo().then((result)=>{
      return result.map((item)=>{
        return <li key={item.id}>{item.body}</li>
      })
    })
    console.log();
    // setTodoItems({todos})
  }, [])

    return(
      <div id="todo-container">
        <p className="container-title red-title">Todo</p>
        <ul id="todo-list">
          {todoItems || <div><div id="spinner"></div> <p style={{color: 'gray'}}>fetching data..</p></div>}
        </ul>
      </div>
    )
}

const mapStateToProps = (state, ownProps)=>{
  return{
    todo: state.todo
  }
}

export default connect(mapStateToProps)(Todo);
