import React, {useState, useEffect} from 'react'
import {connect} from 'react-redux'

import {GET_DONE_REQUESTED, GET_DONE_SUCCEEDED, GET_DONE_FAILED} from '../requests/fetchState'
import {fetchDone} from '../requests/fetchApi'

function Done(props){


  const [done, setDone] = useState('');

  useEffect(()=>{

    const dones = fetchDone().then((result)=>{
      props.doneCommit({type: 'done-data', body: result})
      return result.reverse().map((item)=>{
        return <li key={item.id}>{item.body}</li>
      })
    })
    dones.then((result)=>{
      setDone(result)
    })
  }, [])

  return(
    <div id="done-container">
      <p className="container-title green-title">Done</p>
      <ul id="done-list">
        {done || <div><div id="spinner"></div> <p style={{color: 'gray'}}>fetching data..</p></div> }
      </ul>
    </div>
  )
}

const mapStateToProps = (state, ownProps)=>{
  return{
    done: state.done
  }
}

const mapDispatchToProps = (dispatch)=>{
  return{
    doneCommit: (data)=>{
      return dispatch(data)
    }
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Done)
