import React, {useState, useEffect} from 'react'
import {connect} from 'react-redux'

function InProgress(props){

  const [inProgress, setInProgress] = useState('')

  useEffect(()=>{
    const inprogressTodo = props.inprogress.then((result)=>{
      return result.map((item)=>{
        return <li>{item.body}</li>
      })
    })
    inprogressTodo.then(result=>setInProgress(result))
  }, [])

  return(
    <div id="inprogress-container">
      <p class="container-title orange-title">InProgress</p>
      <ul id="inprogress-list">
        {inProgress || <p style={{color: 'rgba(100, 100, 100, 0.8)', margin: '70px 0px', textShadow: '1px 2px 10px gray'}}>Nothing In Progress !</p>}
      </ul>
    </div>
  )
}

const mapStateToProps = (state, ownProps)=>{

  return{
    inprogress: state.inprogress
  }

}

export default connect(mapStateToProps)(InProgress)
