import React, {useState, useEffect} from 'react'

function Todo(){

  const [todoItems, setTodoItems] = useState()

  const getTodos = async ()=>{
    const callTodos = await fetch('/todos');
    const response = await callTodos.json();

    if(callTodos.status != 200) return new Error(response.message);

    return response.reverse();
  }

  useEffect(()=>{
    getTodos().then(response=>{
      let todosData = response.map((item)=>{
        return <li>{item.body}</li>
      })
      setTodoItems(todosData)
    })
  }, [])

  console.log();

    return(
      <div id="todo-container">
        <p class="container-title red-title">Todo</p>
        <ul id="todo-list">
          {todoItems || <p style={{color: 'rgba(100, 100, 100, 0.8)', margin: '70px 0px', textShadow: '1px 2px 10px gray'}}>Nothing To Do !</p>}
        </ul>
      </div>
    )
}

export default Todo
