import {fetchTodo, fetchInprogress, fetchDone} from '../requests/fetchApi'

const initState = {
  todo: [{id: 0, body: 'hi..'}],
  inprogress: [],
  done: []
};

const rootReducer = (state = initState, action)=>{
  console.log(action);

  switch(action.type){
    case 'todo-data':
      return{
        ...state,
        todo: action.body
      }
      break;

    case 'inprogress-data':
      return{
        ...state,
        inprogress: action.body
      }
      break;

    case 'done-data':
      return{
        ...state,
        done: action.body
      }
      break;

    case 'add_todo':
      return{
        ...state,
        todo: [...state.todo, action.body]
      }
      break;

    default:
      return state;
  }

}

export default rootReducer
