import {fetchTodo, fetchInprogress, fetchDone} from '../requests/fetchApi'

let resultOfTodo = null;
let resultOfInprogress = null;
let resultOfDone = null;

(function(){
  fetchTodo().then((result)=>{resultOfTodo = result.reverse()})
  fetchInprogress().then((result)=>{resultOfInprogress = result.reverse()})
  fetchDone().then((result)=>{resultOfDone = result.reverse()})
})();

const initState = {
  todo: resultOfTodo,
  inprogress: resultOfInprogress,
  done: resultOfDone
};

const rootReducer = (state = initState, action)=>{
  // console.log(action);
  console.log({...state});
  if(action.type == 'add todo'){
    return state
  }else{
    return state
  }

}

export default rootReducer
