import logo from './logo.svg';
import './App.css';

function App() {
  const [text, setText] = useState('some text');

  const callApi = async ()=>{
    const response = await fetch('/api/hello');
    const body = await response.json();

    if(response.status != 200) throw console.error();
  }

  return (
    <div className="App">
      <h2>Call out API</h2>
      <p>{text}</p>
    </div>
  );
}

export default App;
